import React, { useState } from 'react';
import './MainPage.css';
import { Link } from 'react-router-dom';

function MainPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://your-backend-url/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        // Assuming the server sends back a JWT token upon successful login
        sessionStorage.setItem('jwt', data.token); // Store the token in sessionStorage
        // Redirect the user or perform necessary actions after successful login
      } else {
        // Handle login failure (e.g., show an error message)
        console.log('Login failed');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="main-page">
      <header>
        <h1>Explore Otter Weather World</h1>
      </header>
      <main>
        <section className="signup-section">
          <h2>Join us Today!</h2>
          <form onSubmit={handleLogin}>
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <br />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <br />
            <button type="submit" className="signin-btn">Sign In</button>
          </form>
          <p>
            Create account <Link to="/signup" className="signup-btn">click here</Link>
          </p>
        </section>
      </main>
      <footer>
        <p>&copy; 2023 CST438 CSUMB.</p>
      </footer>
    </div>
  );
}

export default MainPage;
